#!/usr/bin/env bash
# Netboot Studio - generate nfs server config


source ./bin/nscommon.bashlib || {
  echo "failed to source nscommon.bashlib"
  exit 1
}

# shellcheck disable=SC1091
source "local_environment.sh" || {
  echo "failed to source local_environment.sh"
  exit 1
}

takenote "rendering /etc/exports"
cat << EOF_NFS > /etc/exports
# auto-generated by NetbootStudio generate_nfs_config.sh
${NS_CONFIGDIR}/boot_images *(ro,fsid=1,sync,no_wdelay,insecure_locks,insecure,no_subtree_check,all_squash,anonuid=1000,anongid=1000)

EOF_NFS
